import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { AuthConstant } from '../constants/auth.constants';
import { EndPointConstants } from '../constants/endpoint.constants';

@Injectable({
  providedIn: 'root',
})
export class NewsService {
  constructor(private httpClient: HttpClient) {}

  public getAllNews(): void {
    const headers1 = {
      'X-Avantio-Aut': AuthConstant.token,
      'Access-Control-Request-Headers': '*',
      Accept: '*/*',
      'Access-Control-Allow-Origin': '*',
    };

    const headers2 = new HttpHeaders();
    headers2.set('X-Avantio-Auth', AuthConstant.token);
    // headers2.set('Access-Control-Request-Headers', '*');
    // headers2.set('Access-Control-Allow-Origin', '*');
    // headers2.set('Accept', '*/*');

    // const headers = new HttpHeaders({
    //   'Access-Control-Allow-Origin': '*',
    //   'Access-Control-Allow-Methods': '*',
    //   'Content-Type': 'application/json',
    // });

    this.httpClient
      .get<any>(EndPointConstants.WebService.newsEndPoint(), {
        headers: headers2,
      })
      .subscribe((data) => {
        console.log(data);
      });
  }
}
